private void cargarComboMaterias(){
    for (Materia materia : materias) {
        jCBoxMateria.addItem(materia);
    }
}
private void cargarComboAlumnos(){
    for (Alumno alumno : alumnos) {
        jCBoxAlumno.addItem(alumno);
    }
}
